import React, { useState, useEffect } from "react";

import "./App.css";

const weapons = ["rock", "paper", "scissors"];

function App() {
  const [winner, setWinner] = useState();
  const [weapon, setWeapon] = useState();
  const [computer, setComputer] = useState();
  const [user, setUser] = useState(0);
  const [bot, setBot] = useState(0);
  const [tie, setTie] = useState("Its Tie Play Again");
  const [you, setYou] = useState("you Won");
  const [lost, setLost] = useState("You lost");

  const start = (event) => {
    event.preventDefault();

    setComputer(weapons[Math.floor(Math.random() * weapons.length)]);

    if (weapon === computer) {
      setWinner(tie);
    } else if (
      (weapon === "rock" && computer === "scissors") ||
      (weapon === "scissors" && computer === "paper") ||
      (weapon === "paper" && computer === "rock")
    ) {
      setWinner(you);
      setUser(user + 1);
    } else {
      setWinner(lost);
      setBot(bot + 1);
    }
  };
  const restart = (event) => {
    event.preventDefault();
    setWinner(null);
    setWeapon(null);
    setBot(0);
    setUser(0);
  };

  return (
    <>
      <div className="app">
        <h1> Rock Paper Scissor</h1>
      </div>
      <div className="app__score">
        <h1>{user}</h1>
        <h1>----</h1>
        <h1>{bot}</h1>
        <table>
          <tr>
            <th>User</th>
            <th>Computer</th>
          </tr>
          <tr>
            <td>{}</td>
          </tr>
        </table>
      </div>
      <div className="app__rpsbutton">
        <button onClick={() => setWeapon("rock")}>Rock</button>
        <button onClick={() => setWeapon("paper")}>Paper</button>
        <button onClick={() => setWeapon("scissor")}>Scissor</button>
      </div>
      <div className="app__selection">
        <h1>{weapon}</h1>
        <h1>{computer}</h1>
        <h1>{winner}</h1>
      </div>
      <div className="app__startbutton">
        <button onClick={start}>Play</button>
        <button onClick={restart}>Restart</button>
      </div>
    </>
  );
}

export default App;
