import React, { useState, useEffect } from "react";

import "./App.css";

const weapons = ["rock", "paper", "scissors"];
let player = ["playerone", "playertwo", "winner"];

function App() {
  const [winner, setWinner] = useState();
  const [weapon, setWeapon] = useState();
  const [computer, setComputer] = useState();
  const [tie, setTie] = useState("Its Tie Play Again");
  const [you, setYou] = useState("you Won");
  const [lost, setLost] = useState("You lost");

  useEffect(() => {
    setWinner(null);
  }, []);

  const start = (event) => {
    event.preventDefault();
    // setComputer({
    //   playerTwo: weapons[Math.floor(Math.random() * weapons.length)],
    // });
    setComputer(weapons[Math.floor(Math.random() * weapons.length)]);
    console.log(computer);
    if (weapon === computer) {
      setWinner(tie);
    } else if (
      (weapon === "rock" && computer === "scissors") ||
      (weapon === "scissors" && computer === "paper") ||
      (weapon === "paper" && computer === "rock")
    ) {
      setWinner("");
    } else {
      setWinner(lost);
    }
  };
  // const winnerDecision = (event) => {
  //   if (weapon === computer) {
  //     setTie("its");
  //   }
  // };

  return (
    <>
      <div className="app">
        <h1> Rock Paper Scissor</h1>
      </div>
      <div className="app__rpsbutton">
        <button onClick={() => setWeapon("rock")}>Rock</button>
        <button onClick={() => setWeapon("paper")}>Paper</button>
        <button onClick={() => setWeapon("scissor")}>Scissor</button>
      </div>
      <div>
        <h1>{weapon}</h1>
        <h1>{winner}</h1>
      </div>
      <div className="app__startbutton">
        <button onClick={start}>Start</button>
      </div>
    </>
  );
}

export default App;
