import React, { useState } from "react";

import "./App.css";

const weapons = ["rock", "paper", "scissors"];

function App() {
  const [winner, setWinner] = useState();
  const [weapon, setWeapon] = useState();
  let player;

  const winnerd = () => {
    player: weapons[Math.floor(Math.random() * weapons.length)];
  };

  const start = (event) => {
    event.preventDefault();
    setWinner(player);
  };
  const selectweapon = (event) => {
    event.preventDefault();
  };

  return (
    <>
      <div className="app">
        <h1> Rock Paper Scissor</h1>
      </div>
      <div className="app__rpsbutton">
        <button onClick={() => setWeapon("Rock")}>Rock</button>
        <button onClick={() => setWeapon("Paper")}>Paper</button>
        <button onClick={() => setWeapon("Scissor")}>Scissor</button>
      </div>
      <div>
        <h1>{weapon}</h1>
        <h1>{winner ? winnerd() : null}</h1>
      </div>
      <div className="app__startbutton">
        <button onClick={start}>Start</button>
      </div>
    </>
  );
}

export default App;
