  const [tie, setTie] = useState("Its Tie Play Again");
  const [you, setYou] = useState("you Won");
  const [lost, setLost] = useState("You lost");

  const start = (event) => {
    event.preventDefault();

    setComputer(weapons[Math.floor(Math.random() * weapons.length)]);
    console.log(computer);
    if (weapon === computer) {
      setWinner(tie);
    } else if (
      (weapon === "rock" && computer === "scissors") ||
      (weapon === "scissors" && computer === "paper") ||
      (weapon === "paper" && computer === "rock")
    ) {
      setWinner(you);
    } else {
      setWinner(lost);
    }
  };
  const restart = (event) => {
    event.preventDefault();
    setWinner(null);
    setWeapon(null);
  };

  return (
    <>
      <div className="app">
        <h1> Rock Paper Scissor</h1>
      </div>
      <div className="app__score">
