import React, { useState } from "react";

import "./App.css";

function App() {
  const [winner, setWinner] = useState("Player 1");

  const start = (event) => {
    event.preventDefault();

    setWinner("Player 2");
  };

  return (
    <>
      <div className="app">
        <h1> Rock Paper Scissor</h1>
      </div>
      <div className="app__rpsbutton">
        <button>Rock</button>
        <button>Paper</button>
        <button>Scissor</button>
      </div>
      <div className="app__startbutton">
        <button onClick={start}>Start</button>
      </div>
    </>
  );
}

export default App;
